import { A as AuroPane } from './auro-pane.min.js';

function focusExample() {
  const focusExample = document.querySelector("#focusExample");
  const focusExampleBtn = document.querySelector("#focusExampleBtn");

  focusExampleBtn.addEventListener("click", () => {
    focusExample.focus();
  });
}

AuroPane.register();

function initExamples(initCount) {
  // biome-ignore lint: no-unused-variables
  initCount = initCount || 0;

  try {
    focusExample();
  } catch (_err) {
    if (initCount <= 20) {
      // setTimeout handles issue where content is sometimes loaded after the functions get called
      setTimeout(() => {
        initExamples(initCount + 1);
      }, 100);
    }
  }
}

export { initExamples };
